package laba1;

public class SysCall {
	private Integer id;
	private String name;
	private String arg;

	public SysCall(int id, String name, String arg) {
		this.id=id;
		this.name=name;
		this.arg=arg;
	}

	public int gId() {
		// TODO Auto-generated method stub
		return id;
	}

	public String gName() {
		// TODO Auto-generated method stub
		return name;
	}

	public String gArgs() {
		// TODO Auto-generated method stub
		return arg;
	}

	
}
